import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import clsx from "clsx";
import axios from "axios";
import { differenceInDays, addDays } from "date-fns";
import { Grid, List, ListItem, ListItemText } from "@material-ui/core";
import { loginLevelState } from "./data/atomdata";
import { atom, selector, useRecoilState, useRecoilValueLoadable } from "recoil";
// import { departmentsSelector } from "../helpers/Recoilhelpers";
import { wpexpiry_url } from "../utils/constants";
import { useEmployees } from "./employees/useEmployees";

const drawerWidth = 240;

const columns = [
  {
    title: "Name",
    field: "name",
    editable: "never",
  },
  {
    title: "Work Permit No",
    field: "workpermitno",
    editable: "never",
  },
  {
    title: "Work Permit Expiry",
    field: "workpermit_expirydate",
    type: "date",
    dateSetting: { locale: "en-GB" },
    editable: "never",
  },
];

const WPExpiryViewStaff = () => {
  const classes = useStyles();
  const fixedHeightPaper = clsx(classes.paper, classes.fixedHeight);
  const { employees, setFilter } = useEmployees();
  const [loginLevel, setLoginLevel] = useRecoilState(loginLevelState);
  const [userdata, setUserdata] = useState([]);

   
  const today = Date().toLocaleString();

  useEffect(() => {
    setFilter(loginLevel.loginUserId);
  }, []);

  return (
    <List className={classes.root}>
      <Grid container direction="row">
        {employees
          .filter(
            (i) =>
              differenceInDays(new Date(today), new Date(i.expiry_date)) < 90
          )
          .map((row) => {
            return (
              <ListItem key={row.id}>
                <Grid item sm={4} align="center">
                  <ListItemText>{row.name}</ListItemText>
                </Grid>
                <Grid item sm={4} align="center">
                  <ListItemText>{row.workpermitno}</ListItemText>
                </Grid>
                <Grid item sm={4} align="center">
                  <ListItemText>{row.workpermit_expirydate}</ListItemText>
                </Grid>
              </ListItem>
            );
          })}
      </Grid>
    </List>
  );
};

const useStyles = makeStyles((theme) => ({
  root: {
    padding: 0,
  },
}));

export default WPExpiryViewStaff;
