import { useState, useCallback } from "react";
import { useQuery } from "react-query";
import { family_url } from "../../utils/constants";
import { filterByEmpId } from "./utils";

import axios from "axios";
import { queryKeys } from "../react-query/constants";

async function getFamily() {
  const [filter, setFilter] = useState("all");

  const selectFn = useCallback(
    (unfiltered) => filterByEmpId(unfiltered, filter),
    [filter]
  );

  const { data } = await axios.get(`${family_url}`);
  return data;
}

export function useFamily() {
  const fallback = [];
  const { data: family = fallback } = useQuery(queryKeys.family, getFamily);

  return { family };
}
