import pdfMake from "pdfmake/build/pdfmake";
import vfsFonts from "pdfmake/build/vfs_fonts";
import { formatPrice } from "../helpers/Utils";

const PrintPDFTest = ({ singlebatchpayslip }) => {
  const names = singlebatchpayslip.map((r) => {
    return r.name;
  });
  const wages = singlebatchpayslip.map((r) => {
    const amount = formatPrice(r.wages);
    return amount;
  });
  const tapamount = singlebatchpayslip.map((r) => {
    const amount = formatPrice(r.tap_amount);
    return amount;
  });
  const scpamount = singlebatchpayslip.map((r) => {
    const amount = formatPrice(r.scp_amount);
    return amount;
  });
  const totalallows = singlebatchpayslip.map((r) => {
    const amount = formatPrice(r.total_allowances);
    return amount;
  });
  const totaldeducts = singlebatchpayslip.map((r) => {
    const amount = formatPrice(r.total_deductions);
    return amount;
  });
  const nettpay = singlebatchpayslip.map((r) => {
    const amount = formatPrice(r.nett_pay);
    return amount;
  });
  const { vfs } = vfsFonts.pdfMake;
  pdfMake.vfs = vfs;

  const documentDefinition = {
    pageSize: "A4",
    pageOrientation: "landscape",
    content: [
      { text: "AppSmith Sutera Sdn Bhd", style: "header" },
      { text: "Payroll Summary", style: "subheader" },

      {
        style: "tableExample",
        table: {
          widths: [150, 80, 80, 80, 80, 80, 80],
          body: [
            [
              "Name",
              { alignment: "right", text: "Wages" },
              { alignment: "right", text: "TAP Amount" },
              { alignment: "right", text: "SCP Amount" },
              { alignment: "right", text: "Allowances" },
              { alignment: "right", text: "Deductions" },
              "Nett Pay",
            ],
            [
              {
                stack: [
                  {
                    type: "none",
                    fontSize: 12,
                    ul: names,
                  },
                ],
              },
              {
                stack: [
                  {
                    type: "none",
                    fontSize: 12,
                    alignment: "right",
                    ul: wages,
                  },
                ],
              },
              {
                stack: [
                  {
                    type: "none",
                    fontSize: 12,
                    alignment: "right",
                    ul: tapamount,
                  },
                ],
              },

              {
                stack: [
                  {
                    type: "none",
                    fontSize: 12,
                    alignment: "right",
                    ul: scpamount,
                  },
                ],
              },
              {
                stack: [
                  {
                    type: "none",
                    fontSize: 12,
                    alignment: "right",
                    ul: totalallows,
                  },
                ],
              },
              {
                stack: [
                  {
                    type: "none",
                    fontSize: 12,
                    alignment: "right",
                    ul: totaldeducts,
                  },
                ],
              },
              {
                stack: [
                  {
                    type: "none",
                    fontSize: 12,
                    alignment: "right",
                    ul: nettpay,
                  },
                ],
              },
            ],
            [
              "Name",
              "Wages",
              "TAP Amount",
              "SCP Amount",
              "Allowances",
              "Deductions",
              "Nett Pay",
            ],
          ],
        },
      },
    ],
    styles: {
      header: {
        fontSize: 18,
        bold: true,
        margin: [0, 0, 0, 10],
      },
      subheader: {
        fontSize: 16,
        bold: true,
        margin: [0, 10, 0, 5],
      },
      tableExample: {
        margin: [0, 5, 0, 15],
      },
      tableOpacityExample: {
        margin: [0, 5, 0, 15],
        fillColor: "blue",
        fillOpacity: 0.3,
      },
      tableHeader: {
        bold: true,
        fontSize: 13,
        color: "black",
      },
    },
    defaultStyle: {
      // alignment: 'justify'
    },
  };

  pdfMake.createPdf(documentDefinition).open();
};

export default PrintPDFTest;
