import pdfMake from "pdfmake/build/pdfmake";
import vfsFonts from "pdfmake/build/vfs_fonts";

const PrintPDFTest = ({ singlebatchpayslip }) => {
  const { vfs } = vfsFonts.pdfMake;
  pdfMake.vfs = vfs;

  const documentDefinition = {
    pageSize: "A4",
    pageOrientation: "potrait",
    content: [
      {
        text: "Payroll Summary\n\n",
        style: "header",
      },

      {
        style: "tableExample",
        color: "#444",
        table: {
          widths: ["auto", "auto", "auto", "auto", "auto", "auto", "auto"],
          headerRows: 2,
          // keepWithHeaderRows: 1,
          body: [
            [
              {
                text: "Header with Colspan = 2",
            content: [
    {
      layout: 'lightHorizontalLines', // optional
      table: {
        // headers are automatically repeated if the table spans over multiple pages
        // you can declare how many rows should be treated as headers
        headerRows: 1,
        widths: [ '*', 'auto', 100, '*' ],

        body: [
          [ 'First', 'Second', 'Third', 'The last one' ],
          [ 'Value 1', 'Value 2', 'Value 3', 'Value 4' ],
          [ { text: 'Bold value', bold: true }, 'Val 2', 'Val 3', 'Val 4' ]
        ]
      }
    }
  ],    style: "tableHeader",
                colSpan: 2,
                alignment: "center",
              },
              {},
              { text: "Header 3", style: "tableHeader", alignment: "center" },
            ],
            [
              { text: "Header 1", style: "tableHeader", alignment: "center" },
              { text: "Header 2", style: "tableHeader", alignment: "center" },
              { text: "Header 3", style: "tableHeader", alignment: "center" },
            ],
            ["Sample value 1", "Sample value 2", "Sample value 3"],
            [
              {
                rowSpan: 3,
                text: "rowSpan set to 3\nLorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor",
              },
              "Sample value 2",
              "Sample value 3",
            ],
            ["", "Sample value 2", "Sample value 3"],
            ["Sample value 1", "Sample value 2", "Sample value 3"],
            [
              "Sample value 1",
              {
                colSpan: 2,
                rowSpan: 2,
                text: "Both:\nrowSpan and colSpan\ncan be defined at the same time",
              },
              "",
            ],
            ["Sample value 1", "", ""],
          ],
        },
      },
    ],
    styles: {
      header: {
        fontSize: 18,
        bold: true,
        margin: [0, 0, 0, 10],
      },
      subheader: {
        fontSize: 16,
        bold: true,
        margin: [0, 10, 0, 5],
      },
      tableExample: {
        margin: [0, 5, 0, 15],
      },
      tableOpacityExample: {
        margin: [0, 5, 0, 15],
        fillColor: "blue",
        fillOpacity: 0.3,
      },
      tableHeader: {
        bold: true,
        fontSize: 13,
        color: "black",
      },
    },
    defaultStyle: {
      // alignment: 'justify'
    },
  };

  pdfMake.createPdf(documentDefinition).open();
};

export default PrintPDFTest;
