          </div>
          <div>
            <Button
              type="submit"
              variant="contained"
              color="primary"
              className={classes.button}
              style={{ marginLeft: 10 }}
              onClick={handleCalc}
            >
              Calc <Icon className={classes.rightIcon}>send</Icon>
            </Button>
          </div>
        </Grid>
        {/* <button>Submit</button> */}
      </Grid>
    </form>
  );
};

