import { Autocomplete } from "@material-ui/lab";
import { makeStyles } from "@material-ui/core/styles";
import { usePayslipsContext } from "../context/payslips_context";

const initial_state = [
  {
    name: "",
    period: "",
    date: "",
    payrun: "",
    nett_pay: 0,
    bank_name: "",
    bank_acno: "",
    tap_acno: "",
    tap_amount: 0,
    scp_acno: "",
    scp_amount: 0,
    total_allowances: 0,
    total_deductions: 0,
    empid: "",
    status: "",
    allows_type1: " ",
    allows_type1amt: 0,
    allows_type2: " ",
    allows_type2amt: 0,
    allows_type3: " ",
    allows_type3amt: 0,
    allows_type4: " ",
    allows_type4amt: 0,
    allows_type5: " ",
    allows_type5amt: 0,
    allows_type6: " ",
    allows_type6amt: 0,
    allows_type7: "",
    allows_type7amt: 0,
    allows_type8: "",
    allows_type8amt: 0,
    deducts_type1: "",
    deducts_type1amt: 0,
    deducts_type2: " ",
    deducts_type2amt: 0,
    deducts_type3: " ",
    deducts_type3amt: 0,
    deducts_type4: "",
    deducts_type4amt: 0,
    deducts_type5: "",
    deducts_type5amt: 0,
    deducts_type6: "",
    deducts_type6amt: 0,
    deducts_type7: "",
    deducts_type7amt: 0,
    deducts_type8: "",
    deducts_type8amt: 0,
  },
];

const PayForm = ({
  formdata,
  setFormdata,
  loadFormdata,
  setLoadFormdata,
  payitems,
  setLoadUpdatedata,
  rowindex,
  isCalc,
  setIsCalc
