              </div>
            </Grid>
          </Grid>
        </Grid>
        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <div>
            <h3>{state.name}</h3>
          </div>
          <Divider variant="fullWidth" className={classes.divider} />
          <div>
            <TextField
              label="Wages"
              name="wages"
              variant="filled"
              type="number"
              value={state.wages}
              onChange={handleChange}
              style={{ width: "100%" }}
              InputLabelProps={{
                shrink: true,
              }}
              InputProps={{
                readOnly: true,
              }}
            />
          </div>
          <div>
            <TextField
              label="TAP Amount"
              name="tap_amount"
              variant="filled"
              type="number"
              value={state.tap_amount}
              onChange={handleChange}
              style={{ width: "100%" }}
              InputLabelProps={{
                shrink: true,
              }}
              InputProps={{
                readOnly: true,
              }}
            />
          </div>
          <div>
            <TextField
              label="SCP Amounut"
              name="scp_amount"
              variant="filled"
              type="number"
              value={state.scp_amount}
              onChange={handleChange}
              style={{ width: "100%" }}
              InputLabelProps={{
                shrink: true,
              }}
              InputProps={{
                readOnly: true,
              }}
            />
          </div>
          <div>
            <TextField
              label="Total Allowances"
              name="total_allowances"
              variant="filled"
              type="number"
              value={state.total_allowances}
              onChange={handleChange}
              style={{ width: "100%" }}
              InputLabelProps={{
                shrink: true,
              }}
              InputProps={{
                readOnly: true,
              }}
            />
          </div>
          <div>
            <TextField
              label="Total Deductions"
              name="total_deductions"
              variant="filled"
              type="number"
              value={state.total_deductions}
              onChange={handleChange}
              style={{ width: "100%" }}
              InputLabelProps={{
                shrink: true,
              }}
              InputProps={{
                readOnly: true,
              }}
            />
          </div>
          <div>
            <TextField
              label="Nett Pay"
              name="nett_pay"
              variant="filled"
              type="number"
              value={state.nett_pay}
              onChange={handleChange}
              style={{ width: "100%" }}
              InputLabelProps={{
                shrink: true,
              }}
              InputProps={{
                readOnly: true,
              }}
            />
          </div>
          <div>
            <Button
              type="submit"
              variant="contained"
              color="primary"
              className={classes.button}
              style={{ marginLeft: 10 }}
              onClick={handleCalc}
            >
              Calc <Icon className={classes.rightIcon}>send</Icon>
            </Button>
          </div>
        </Grid>
        {/* <button>Submit</button> */}
      </Grid>
    </form>
  );
};

