import React, { useEffect } from "react";
import MaterialTable from "material-table";
import { makeStyles } from "@material-ui/core/styles";
import { useQueryClient, useMutation } from "react-query";
import { departments_url } from "../utils/constants";
import { useDepartments } from "./departments/useDepartments";
import { useRecoilState, useRecoilValueLoadable } from "recoil";
import { fetchDepartmentsSelector } from "./data/selectordata";
import { deptDataState } from "./data/atomdata";
import { useTablesContext } from "../context/tables_context";
import { useUpdateDepartments } from "./departments/useUpdateDepartments"
import { useDeleteDepartments } from "./departments/useDeleteDepartments";

const columns = [
  {
    title: "Name",
    field: "name",
  },
];

export default function UpdateDepartments() {
  const classes = useStyles();
  const { departments } = useDepartments();
  const queryClient = useQueryClient();
  const updateDepartments = useUpdateDepartments();
const updateMutation = useUpdateDepartments();
  const {
    //loadDepartments,
    //departments,
    departments_loading,
    addDepartment,
    deleteDepartment,
    // updateDepartment,
  } = useTablesContext();
  
  const update_Department = (data) => {
    const {id, rec_id,...fields} = data
    updateMutation({ id,  ...fields });
    //loadDepartments();
  };

  const add_Department = (data) => {
    addDepartment(data);
    //loadDepartments();
  };

  const delete_Department = (data) => {
    const { id } = data;
    deleteDepartment(id);
    //loadDepartments();
  };

  
  return (
    <div className={classes.root}>
      <div style={{ maxWidth: "100%", paddingTop: "5px" }}>
        <MaterialTable
          columns={columns}
          data={departments}
          title="Departments"
          editable={{
            onRowAdd: (newData) =>
              new Promise((resolve, reject) => {
                setTimeout(() => {
                  add_Department(newData);
                  resolve();
                }, 1000);
              }),
            onRowUpdate: (newData, oldData) =>
              new Promise((resolve, reject) => {
                setTimeout(() => {
                  update_Department(newData);

                  resolve();
                }, 1000);
              }),
            onRowDelete: (oldData) =>
              new Promise((resolve, reject) => {
                setTimeout(() => {
                  delete_Department(oldData);
                  resolve();
                }, 1000);
              }),
          }}
          options={{
            filtering: true,
            headerStyle: {
              backgroundColor: "orange",
              color: "primary",
            },
            showTitle: true,
          }}
        />
      </div>
    </div>
  );
}
const useStyles = makeStyles((theme) => ({
  root: {
    padding: 0,
  },
}));
