import pdfMake from "pdfmake/build/pdfmake";
import vfsFonts from "pdfmake/build/vfs_fonts";
import { formatPrice } from "../helpers/Utils";

const PrintPDFTest = ({ singlebatchpayslip }) => {
  const names = singlebatchpayslip.map((r) => {
    return r.name;
  });
  const wages = singlebatchpayslip.map((r) => {
    const amount = formatPrice(r.wages);
    return amount;
  });
  const { vfs } = vfsFonts.pdfMake;
  pdfMake.vfs = vfs;

  const documentDefinition = {
    pageSize: "A4",
    pageOrientation: "potrait",
    content: [
      { text: "App", style: "header" },
      "Official documentation is in progress, this document is just a glimpse of what is possible with pdfmake and its layout engine.",
      {
        text: "A simple table (no headers, no width specified, no spans, no styling)",
        style: "subheader",
      },
      "The following table has nothing more than a body array",
      {
        style: "tableExample",
        table: {
          body: [
            [
              "Name",
              "Wages",
              "TAP Amount",
              "SCP AMount",
              "Allowances",
              "Deductions",
              "Nett Pay",
            ],
            [
              {
                stack: [
                  {
                    type: "none",
                    fontSize: 12,
                    alignment: "left",
                    ul: names,
                  },
                ],
              },
              {
                stack: [
                  {
                    type: "none",
                    fontSize: 12,
                    alignment: "right",
                    ul: wages,
                  },
                ],
              },

              "OK?",
              "OK",
              "Col 5",
              "Col 6",
              "Col 7",
            ],
          ],
        },
      },
    ],
    styles: {
      header: {
        fontSize: 18,
        bold: true,
        margin: [0, 0, 0, 10],
      },
      subheader: {
        fontSize: 16,
        bold: true,
        margin: [0, 10, 0, 5],
      },
      tableExample: {
        margin: [0, 5, 0, 15],
      },
      tableOpacityExample: {
        margin: [0, 5, 0, 15],
        fillColor: "blue",
        fillOpacity: 0.3,
      },
      tableHeader: {
        bold: true,
        fontSize: 13,
        color: "black",
      },
    },
    defaultStyle: {
      // alignment: 'justify'
    },
  };

  pdfMake.createPdf(documentDefinition).open();
};

export default PrintPDFTest;
