import React, { useEffect } from "react";
import MaterialTable, { MTableToolbar } from "material-table";
import { Button, Icon, TextField } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { useEmployeesContext } from "../context/employees_context";
import { useTablesContext } from "../context/tables_context";
import { useExperiences } from "./educations/useExperiences";
import { useUpdateExperiences } from "./educations/useUpdateExperiences";
import { useAddExperiences } from "./educations/useAddExperiences";
import { useDeleteExperiences } from "./educations/useDeleteExperiences";

const columns = [
  {
    title: "Company",
    field: "company",
  },
  {
    title: "Location",
    field: "location",
  },
  {
    title: "Position",
    field: "position",
  },
  {
    title: "From Date",
    field: "from_date",
    type: "date",
    editComponent: (props) => (
      <TextField
        //defaultValue={props.value || new Date()}
        onChange={(e) => props.onChange(e.target.value)}
        type="date"
      />
    ),
  },
  {
    title: "To Date",
    field: "to_date",
    type: "date",
    editComponent: (props) => (
      <TextField
        //defaultValue={props.value || new Date()}
        onChange={(e) => props.onChange(e.target.value)}
        type="date"
      />
    ),
  },
  { title: "Remark", field: "remark" },
];

export default function Emp_Experiences({
  experiencedata,
  setExperiencedata,
  handleDialogClose,
}) {
  const classes = useStyles();
   const { experiences, setExperienceId } = useExperiences();
   const updateExperiences = useUpdateExperiences();
   const addExperiences = useAddExperiences();
   const deleteExperiences = useDeleteExperiences();
  const { editEmployeeID } = useEmployeesContext();
  const {
    loadSingleBatchExperience,
    singlebatchexperience,
    //addExperience,
    //deleteExperience,
    //updateExperience,
    singlebatch_experience_loading,
  } = useTablesContext();

  useEffect(() => {
   setExperienceId(editEmployeeID)
  }, []);

  const update_Experience = (data) => {
    const { id, rec_id, tableData, ...fields } = data;
       updateExperiences({ id, ...fields });
   
  };

  const add_Experience = (data) => {
    addExperiences({ ...data, empid: editEmployeeID });
   };

  const delete_Experience = (data) => {
    const { id } = data;
    deleteExperiences(id);
  };

  
  return (
    <div className={classes.root}>
      <div style={{ maxWidth: "100%", paddingTop: "5px" }}>
        <MaterialTable
          columns={columns}
          data={experiences}
          title="Experience"
          editable={{
            onRowAdd: (newData) =>
              new Promise((resolve, reject) => {
                setTimeout(() => {
                  add_Experience(newData)
                  resolve();
                }, 1000);
              }),
            onRowUpdate: (newData, oldData) =>
              new Promise((resolve, reject) => {
                setTimeout(() => {
                  // const dataUpdate = [...experiencedata];
                  // const index = oldData.tableData.id;
                  // dataUpdate[index] = newData;
                  // setExperiencedata([...dataUpdate]);
                  update_Experience(newData)
                  resolve();
                }, 1000);
              }),
            onRowDelete: (oldData) =>
              new Promise((resolve, reject) => {
                setTimeout(() => {
                  // const dataDelete = [...experiencedata];
                  // const index = oldData.tableData.id;
                  // dataDelete.splice(index, 1);
                  // setExperiencedata([...dataDelete]);
                  delete_Experience(oldData)
                  resolve();
                }, 1000);
              }),
          }}
          options={{
            filtering: true,
            headerStyle: {
              backgroundColor: "orange",
              color: "primary",
            },
            showTitle: true,
          }}
          components={{
            Toolbar: (props) => (
              <div>
                <MTableToolbar {...props} />
               
              </div>
            ),
          }}
        />
      </div>
    </div>
  );
}

const useStyles = makeStyles((theme) => ({
  root: {
    padding: 0,
  },
}));
