import pdfMake from "pdfmake/build/pdfmake";
import vfsFonts from "pdfmake/build/vfs_fonts";
import { formatPrice } from "../helpers/Utils";
import { useRecoilState, useRecoilValue } from "recoil";
import { payrunState } from "./data/atomdata";
import { usePayslipsContext } from "../context/payslips_context";
import { useEmployees } from "./employees/useEmployees";

const PrintPayslip = ({ data, emp }) => {
  console.log("payslip", data);
  console.log("payslip emp", emp);
  const { name } = data.rowData;
  const { empno, address, phone } = emp[0].address
  const { vfs } = vfsFonts.pdfMake;
  pdfMake.vfs = vfs;

  const documentDefinition = {
    pageSize: "A4",
    pageOrientation: "landscape",
    content: [
      { text: "AppSmith Sutera Sdn Bhd", style: "header" },
      { text: "Payslip", style: "subheader" },
      { text: "Emp No: " + name, style: "text" },
      { text: "Name: " + name, style: "text" },
      { text: "Addess: " + address, style: "text" },
      {
        style: "tableExample",
        table: {
          widths: [150, 80, 80, 80, 80, 80, 80],
          body: [
            [
              "Name",
              { alignment: "right", text: "Wages" },
              { alignment: "right", text: "TAP Amount" },
              { alignment: "right", text: "SCP Amount" },
              { alignment: "right", text: "Allowances" },
              { alignment: "right", text: "Deductions" },
              { alignment: "right", text: "Nett Pay" },
            ],
          ],
        },
      },
    ],
    styles: {
      header: {
        fontSize: 18,
        bold: true,
        margin: [0, 0, 0, 10],
      },
      subheader: {
        fontSize: 16,
        bold: true,
        margin: [0, 10, 0, 5],
      },
      text: {
        fontSize: 12,
        bold: false,
        margin: [0, 10, 0, 5],
      },
      tableExample: {
        margin: [0, 5, 0, 15],
      },
      tableOpacityExample: {
        margin: [0, 5, 0, 15],
        fillColor: "blue",
        fillOpacity: 0.3,
      },
      tableHeader: {
        bold: true,
        fontSize: 13,
        color: "black",
      },
    },
    defaultStyle: {
      // alignment: 'justify'
    },
  };

  pdfMake.createPdf(documentDefinition).open();
};

export default PrintPayslip;
