import pdfMake from "pdfmake/build/pdfmake";
import vfsFonts from "pdfmake/build/vfs_fonts";
import { formatPrice } from "../helpers/Utils";
import { useRecoilState, useRecoilValue } from "recoil";
import { payrunState } from "./data/atomdata";
import { usePayslipsContext } from "../context/payslips_context";
import { useEmployees } from "./employees/useEmployees";

const PrintPayslip = ({ data, emp }) => {
  console.log("payslip", data);
  console.log("payslip emp", emp);
  const { name,wages, period, allows_type1, allows_type2, allows_type3, allows_type4, allows_type5  } = data.rowData;
  const { empno, address, phone } = emp[0];
  const earndesp = ["Wages", allows_type1, allows_type2, allows_type3, allows_type4, allows_type5]
  const earnamt = [
    "Wages",
    allows_type1,
    allows_type2,
    allows_type3,
    allows_type4,
    allows_type5,
  ];
  const subtitle = "Payslips for the period " + period
  const { vfs } = vfsFonts.pdfMake;
  pdfMake.vfs = vfs;

  const documentDefinition = {
    pageSize: "A4",
    pageOrientation: "landscape",
    content: [
      { text: "AppSmith Sutera Sdn Bhd", style: "header" },
      { text: subtitle, style: "subheader" },
      {
        text: [
          { text: "Emp No: " + empno + "\n", style: "text" },
          { text: "Name: " + name + "\n", style: "text" },
          { text: "Addess: " + address + "\n", style: "text" },
          { text: "Phone: " + phone + "\n", style: "text" },
        ],
      },

      {
        style: "tableExample",
        table: {
          widths: [200, 80, 200, 80],
          body: [
            [
              { alignment: "left", text: "Earnings" },
              { alignment: "right", text: "Amount" },
              { alignment: "left", text: "Deductions" },
              { alignment: "right", text: "Amount" },
            ],
            [
              {
                stack: [
                  {
                    type: "none",
                    fontSize: 12,
                    ul: earnings,
                  },
                ],
              },
              { alignment: "right", text: "Amount" },
              { alignment: "left", text: "Deductions" },
              { alignment: "right", text: "Amount" },
            ],
          ],
        },
      },
    ],
    styles: {
      header: {
        fontSize: 18,
        bold: true,
        margin: [0, 0, 0, 10],
      },
      subheader: {
        fontSize: 16,
        bold: true,
        margin: [0, 10, 0, 5],
      },
      text: {
        fontSize: 12,
        bold: false,
        margin: [0, 10, 0, 5],
      },
      tableExample: {
        margin: [0, 5, 0, 15],
      },
      tableOpacityExample: {
        margin: [0, 5, 0, 15],
        fillColor: "blue",
        fillOpacity: 0.3,
      },
      tableHeader: {
        bold: true,
        fontSize: 13,
        color: "black",
      },
    },
    defaultStyle: {
      // alignment: 'justify'
    },
  };

  pdfMake.createPdf(documentDefinition).open();
};

export default PrintPayslip;
