import React from "react";
import {
  Button,
  Icon,
  TextField,
  Paper,
  Typography,
  Grid,
  Divider,
} from "@material-ui/core";
//import { Alert, AlertTitle } from "@material-ui/lab";
import { makeStyles } from "@material-ui/core/styles";
import MenuItem from "@material-ui/core/MenuItem";
import { useEmployeesContext } from "../context/employees_context";
import { Controller, useForm } from "react-hook-form";
import { useRecoilState } from "recoil";
import { loginLevelState } from "./data/atomdata";

const initial_values = {
  name: "",
  gender: "",
  ic_no: "",
  email: "",
  age: 0,
  basic_salary: 0,
  bank_name: "",
  bank_acno: "",
  tap_acno: "",
  scp_acno: "",
  date_of_join: null,
  date_of_resign: null,
  designation: "",
  department: "",
  passportno: "",
  passport_expirydate: null,
  workpermitno: "",
  workpermit_expirydate: null,
  nationality: "",
  marital_status: "",
  siteallows_fee: 0,
  perdiem_fee: 0,
  reporting_to: "",
};

const Emp_Personal = ({ handleDialogClose }) => {
  const classes = useStyles();
  const {
    isEditing,
    single_employee,
    updateEmployee,
    addEmployee,
    editEmployeeID,
    single_employee_loading,
    getSingleEmployee,
  } = useEmployeesContext();
  const {
    name,
    ic_no,
    ic_expirydate,
    gender,
    age,
    email,
    basic_salary,
    bank_name,
    bank_acno,
    tap_acno,
    scp_acno,
    date_of_join,
    date_of_resign,
    designation,
    department,
    passportno,
    passport_expirydate,
    workpermitno,
    workpermit_expirydate,
    nationality,
    marital_status,
    siteallows_fee,
    perdiem_fee,
    reporting_to,
  } = single_employee || initial_values;
  const { handleSubmit, control } = useForm();
  const [loginLevel, setLoginLevel] = useRecoilState(loginLevelState);
  
  const onSubmit = (data) => {
    if (isEditing) {
      updateEmployee({ id: editEmployeeID, ...data });
    } else {
      addEmployee({ ...data });
    }
    setLoginLevel({
      ...loginLevel,
      loginEmail: data.email,
      leave_bal: data.leave_bal,
      siteallows_fee: data.siteallows_fee,
      perdiem_fee: data.perdiem_fee,
      reporting_to: data.reporting_to,
    });
    getSingleEmployee(loginLevel.loginUserId);
    handleDialogClose();
    //loadEmployees();
    // <Alert severity="success">
    //   <AlertTitle>Success</AlertTitle>
    //   This is a success alert — <strong>check it out!</strong>
    // </Alert>;
    //history.push("/allemployees");
  };

  if (single_employee_loading) {
    return <div>Loading...</div>;
  }
  return (
    <div>
      <form onSubmit={handleSubmit(onSubmit)}>
        <Paper className={classes.root}>
          <Grid container className={classes.root} spacing={15}>
            <div>
              <Typography variant="h5" component="h3">
                EMPLOYEE FORM
              </Typography>

              <Button
                type="submit"
                variant="contained"
                color="primary"
                className={classes.button}
              >
                Submit <Icon className={classes.rightIcon}>send</Icon>
              </Button>
            </div>
          </Grid>
          <Divider className={classes.divider} />
          <Grid item xs={12}>
            <div>
              <div>
                <Controller
                  name="name"
                  control={control}
                  defaultValue={name}
                  render={({
                    field: { onChange, value },
                    fieldState: { error },
                  }) => {
                    return (
                      <TextField
                        label="Name"
                        id="margin-normal"
                        name="name"
                        defaultValue={name}
                        className={classes.textField}
                        onChange={onChange}
                        error={!!error}
                        helperText={error ? error.message : null}
                      />
                    );
                  }}
                  rules={{ required: "Name required" }}
                />

                <Controller
                  name="email"
                  control={control}
                  defaultValue={email}
                  render={({
                    field: { onChange, value },
                    fieldState: { error },
                  }) => {
                    return (
                      <TextField
                        label="Email"
                        id="margin-normal"
                        name="email"
                        defaultValue={email}
                        className={classes.textField}
                        onChange={onChange}
                        error={!!error}
                        helperText={error ? error.message : null}
                      />
                    );
                  }}
                  rules={{ required: "Email is required" }}
                />
                <div>
                  <Controller
                    name="ic_no"
                    control={control}
                    defaultValue={ic_no}
                    render={({
                      field: { onChange, value },
                      fieldState: { error },
                    }) => {
                      return (
                        <TextField
                          label="IC No"
                          id="margin-normal"
                          name="ic_no"
                          defaultValue={ic_no}
                          className={classes.textField}
                          onChange={onChange}
                          error={!!error}
                          helperText={error ? error.message : null}
                        />
                      );
                    }}
                    //rules={{ required: "IC No required" }}
                  />
                  <Controller
                    name="ic_expirydate"
                    control={control}
                    defaultValue={ic_expirydate}
                    render={({
                      field: { onChange, value },
                      fieldState: { error },
                    }) => {
                      return (
                        <TextField
                          label="IC Expiry Date"
                          id="margin-normal"
                          name="ic_expirydate"
